import React, { useEffect, useState } from 'react';
import { Row } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('https://api.escuelajs.co/api/v1/products')
      .then((response) => response.json())
      .then((data) => {
        const firstFiveProducts = data.slice(0, 5);
        setProducts(firstFiveProducts);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <>
      <Row>
          <div style={{ width: '100%', height: '100vh' }} className='bg-primary d-flex align-items-center justify-content-center justify-content-around'>
            {products.map((product) => (
              <Card style={{ width: '18rem' }} key={product.id}>
                {/* Use the first image URL in the images array */}
                <Card.Img variant="top" style={{width:"100%" , height:"300px"}} src={product.images && product.images.length > 0 ? product.images[0] : ''} />
                <Card.Body>
                  <Card.Title>{product.title}</Card.Title>
                  <Card.Text>
                    {product.description}
                  </Card.Text>
                  <Button variant="primary">Go somewhere</Button>
                </Card.Body>
              </Card>
            ))}
          </div>
      </Row>
    </>
  );
}

export default Home;
