import React from 'react'
import { FaBars, FaTimes } from "react-icons/fa";
import "./Header.css";
import { Link } from "react-router-dom";


function Header() {
    const navRef = useRef();
	const showNavbar = () => {
		navRef.current.classList.toggle(
			"responsive_nav"
		);
	};
  return (
    <>
    <header >
			<h3 id="h3" className="text-light">BOOKIES</h3>
			<nav ref={navRef}>
			
				<div>
					 <Link  to={'/'}>Home</Link>
					<Link to={'Sellbook'} href="#">Sell Book</Link></div>		
				<button
					className="nav-btn nav-close-btn"
					onClick={showNavbar}>
					<FaTimes />
				</button>
			</nav>
			<button
				className="nav-btn"
				onClick={showNavbar}>
				<FaBars />
			</button>
		</header>
    </>
  )
}

export default Header
