import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('https://api.escuelajs.co/api/v1/products')
      .then((response) => response.json())
      .then((data) => {
        // Take only the first 5 products
        const firstFiveProducts = data.slice(0, 5);
        setProducts(firstFiveProducts);
        console.log(firstFiveProducts);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <>
      <div style={{ width: '100%', height: '100vh' }} className='bg-primary p-5 d-flex justify-content-around'>
        {products.map((product) => (
          <Card style={{ width: '18rem',height:"18rem" }} key={product.id}>
            <Card.Img style={{ width: 'auto',height:"18rem" }} variant="top" src={product.image} />
            <Card.Body>
              <Card.Title>{product.title}</Card.Title>
              <Card.Text>
                <h4>Price :  {product.price} </h4>
             
              </Card.Text>
              <Button variant="primary">Go somewhere</Button>
            </Card.Body>
          </Card>
        ))}
      </div>
    </>
  );
}

export default Home;
