import React, { useEffect, useState } from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

// ... (other imports and code)

function Home() {
    const [products, setProducts] = useState([]);
    const [addwish, setAddWish] = useState([]);
    const [addthings, setAddThings] = useState([]);
  
    useEffect(() => {
      fetch('https://fakestoreapi.com/products')
        .then((response) => response.json())
        .then((data) => {
          const firstFiveProducts = data.slice(10, 24);
          setProducts(firstFiveProducts);
          console.log(firstFiveProducts);
        })
        .catch((error) => {
          console.error('Error fetching data:', error);
        });
    }, []);
  
    const handleCart = (e, it) => {
      e.preventDefault();
      setAddThings(addthings.concat(it));
    };
  
    const handlewish = (e, it) => {
      e.preventDefault();
      setAddWish(addwish.concat(it));
    };
  
    return ( 
      <>
      <div style={{ width: '100%', height: 'auto' }} className='bg-dark'>
           <div style={{ width: 'auto', height: 'auto' }} className='m-5 bg-dark d-flex align-items-center justify-content-center justify-content-around flex-wrap'>
            {products.map((product) => (
              <Card style={{ width: '18rem',height:"auto", marginBottom: '20px' }} key={product.id}>
                {/* Use the image URL directly */}
                <Card.Img variant="top" style={{ width: '100%', height: '250px' }} src={product.image} />
                <Card.Body>
                  <Card.Title class="overflow-hidden" style={{ width: '100%', height: '100px' }}>{product.title}</Card.Title>
                  <Card.Text><h4>Price : {product.price}</h4></Card.Text>
                 <div className='d-flex flex-column '> <Button variant="primary" onClick={e=>handlewish(e,products)}>Add to Wishlist</Button><Button onClick={e=>handleCart(e,products)} className='mt-3' variant="primary">Add to cart</Button></div>
                </Card.Body>
              </Card>
            ))}
          </div>
      </div>
      </>
    );
  }
  

  
 


export default Home; // Ensure this line is at the end of your file
