import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [tshirts, setTshirts] = useState([]);
  const [cart, setCart] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Fetch T-shirt data from API
    const fetchTshirts = async () => {
      try {
        const response = await axios.get('your-tshirt-api-endpoint');
        setTshirts(response.data);
      } catch (error) {
        console.error('Error fetching T-shirts:', error);
      }
    };

    fetchTshirts();
  }, []);

  const addToCart = (tshirt) => {
    setCart([...cart, tshirt]);
  };

  const addToWishlist = (tshirt) => {
    setWishlist([...wishlist, tshirt]);
  };

  const moveFromWishlistToCart = (tshirt) => {
    setWishlist(wishlist.filter((item) => item.id !== tshirt.id));
    setCart([...cart, tshirt]);
  };

  const searchTshirts = () => {
    // Implement search logic based on the searchTerm
    // You can filter the tshirts array based on your criteria
    // and update the state with the filtered results
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Search T-shirts"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={searchTshirts}>Search</button>
      </div>
      <div>
        {tshirts.map((tshirt) => (
          <div key={tshirt.id}>
            <p>{tshirt.name}</p>
            <p>${tshirt.price}</p>
            <button onClick={() => addToCart(tshirt)}>Add to Cart</button>
            <button onClick={() => addToWishlist(tshirt)}>Add to Wishlist</button>
          </div>
        ))}
      </div>
      <div>
        <h2>Cart</h2>
        {cart.map((item) => (
          <div key={item.id}>
            <p>{item.name}</p>
            <p>${item.price}</p>
          </div>
        ))}
      </div>
      <div>
        <h2>Wishlist</h2>
        {wishlist.map((item) => (
          <div key={item.id}>
            <p>{item.name}</p>
            <p>${item.price}</p>
            <button onClick={() => moveFromWishlistToCart(item)}>Move to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
