// Cart.js
import React, { useContext } from 'react';
import { Col, Row } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import { addcontext } from '../';

function Cart() {
  const { addthings } = useContext(addcontext);

  return (
    <>
      {addthings.length > 0 ? (
        <div className='mt-5 ms-5'>
          <Row>
            {addthings.map((product) => (
              <Col sm={12} md={6} lg={4} xl={3} key={product.id}>
                <Card style={{ width: '18rem' }} className='mb-4 shadow rounded'>
                  <Card.Img variant="top" src={product.image} style={{ height: '200px', width: '100%' }} />
                  <Card.Body>
                    <Card.Title>{product.title.slice(0, 16)}</Card.Title>
                    <Card.Text>
                      $ {product.price}
                    </Card.Text>
                    <div className='d-flex justify-content-center'>
                      {/* Additional buttons if needed */}
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            ))}
          </Row>
        </div>
      ) : (
        <h2 className='text-danger text-center mt-5'>Cart Empty</h2>
      )}
    </>
  );
}

export default Cart;
